// ========================================
// CHECK INTERVIEW FOLLOW-UP ELIGIBILITY & PREPARE UPDATES
// ========================================

const rows = $input.all();
const eligibleCandidates = [];
const now = new Date();

// Follow-up timing configuration (in hours)
const FIRST_FOLLOWUP = 48;   // 48 hours (2 days)
const SECOND_FOLLOWUP = 96;  // 96 hours (4 days)
const THIRD_FOLLOWUP = 144;  // 144 hours (6 days)

for (const row of rows) {
  const data = row.json;

  // Get email and validate
  const email = data['Candidate Email'] || '';

  // Skip invalid emails
  if (!email || 
      email === 'empty' || 
      email.includes('#ERROR') || 
      email.includes('()') ||
      !email.includes('@')) {
    console.log(`Skipping row - invalid email: ${email}`);
    continue;
  }

  // Skip rows without candidate name
  const candidateName = data['Candidate Name'] || '';
  if (!candidateName || candidateName === 'empty') {
    console.log(`Skipping row - no candidate name`);
    continue;
  }

  // Skip rows without interview date
  const interviewDateStr = data['Interview Date'] || '';
  if (!interviewDateStr || interviewDateStr === 'empty') {
    console.log(`Skipping row - no interview date`);
    continue;
  }

  // Check if interview hasn't been completed
  const isIncomplete = !data['Duration (minutes)'] || 
                       !data['Call ID'] || 
                       !data['Analyzed On'] ||
                       data['Duration (minutes)'] === 'empty' ||
                       data['Call ID'] === 'empty' ||
                       data['Analyzed On'] === 'empty';

  // Skip if interview is already completed
  if (!isIncomplete) {
    console.log(`Skipping ${candidateName} - interview already completed`);
    continue;
  }

  // Parse the interview date (handle both DD/MM/YYYY and YYYY-MM-DD formats)
  let interviewDate;
  if (interviewDateStr.includes('/')) {
    const dateParts = interviewDateStr.split('/');
    interviewDate = new Date(
      parseInt(dateParts[2]), // year
      parseInt(dateParts[1]) - 1, // month (0-indexed)
      parseInt(dateParts[0]) // day
    );
  } else if (interviewDateStr.includes('-')) {
    interviewDate = new Date(interviewDateStr);
  } else {
    console.log(`Skipping ${candidateName} - invalid date format: ${interviewDateStr}`);
    continue;
  }

  // Validate parsed date
  if (isNaN(interviewDate.getTime())) {
    console.log(`Skipping ${candidateName} - could not parse date: ${interviewDateStr}`);
    continue;
  }

  // Calculate hours since interview was sent
  const hoursSinceInvite = (now - interviewDate) / (1000 * 60 * 60);

  // Check current follow-up status
  const currentStatus = data['Interview Follow Up Status'] || '';

  // Determine which follow-up to send
  let followUpType = null;
  let shouldSend = false;

  if (hoursSinceInvite >= THIRD_FOLLOWUP && !currentStatus.includes('3rd')) {
    followUpType = '3rd';
    shouldSend = true;
  } else if (hoursSinceInvite >= SECOND_FOLLOWUP && !currentStatus.includes('2nd')) {
    followUpType = '2nd';
    shouldSend = true;
  } else if (hoursSinceInvite >= FIRST_FOLLOWUP && !currentStatus.includes('1st')) {
    followUpType = '1st';
    shouldSend = true;
  }

  // Only add if a follow-up is needed
  if (shouldSend && followUpType) {
    console.log(`✓ ${candidateName} (${email}) - ${followUpType} follow-up needed`);

    eligibleCandidates.push({
      // Row identification
      row_number: data.row_number,
      rowId: data.row_number,

      // Candidate info
      candidateName: candidateName,
      candidateEmail: email,
      Email: email,
      interviewLink: data['Interview Link'] || '',
      interviewDate: interviewDateStr,
      agentName: data['Agent Name'],

      // Follow-up details
      followUpType: followUpType,
      currentStatus: currentStatus,
      hoursSinceInvite: Math.floor(hoursSinceInvite),

      // Metadata
      status: `${followUpType} Follow-up Needed`,
      updateTimestamp: now.toISOString()
    });
  }
}

// Return results
if (eligibleCandidates.length === 0) {
  return [{
    json: {
      message: 'No candidates need follow-up at this time',
      checkedAt: now.toISOString(),
      totalChecked: rows.length,
      eligibleCount: 0
    }
  }];
}

return eligibleCandidates.map(candidate => ({
  json: candidate
}));

