const now = new Date();
const currentDateUK = new Date(now.toLocaleString('en-US', { timeZone: 'Europe/London' }));
const STATUS_COLUMN = "AI Interview Status";
const oneDay = 24 * 60 * 60 * 1000;

function parseTimestamp(timestampStr) {
  if (!timestampStr) return null;
  if (timestampStr instanceof Date) return timestampStr;
  if (typeof timestampStr === 'string' && (timestampStr.includes('-') || timestampStr.includes('T'))) {
    const iso = new Date(timestampStr);
    if (!isNaN(iso.getTime())) return iso;
  }
  if (typeof timestampStr === 'string' && timestampStr.includes('/')) {
    try {
      const parts = timestampStr.split(',')[0].trim().split('/');
      if (parts.length === 3) {
        return new Date(parseInt(parts[2]), parseInt(parts[1]) - 1, parseInt(parts[0]));
      }
    } catch (e) { return null; }
  }
  return new Date(timestampStr);
}

function checkEligibility(candidate) {
  const currentStatus = candidate[STATUS_COLUMN] || "";

  if (currentStatus && (
      currentStatus.toString().toLowerCase().includes('scheduled') || 
      currentStatus.toString().toLowerCase().includes('sent') ||
      currentStatus.toString().toLowerCase().includes('done')
     )) {
    return { eligible: false, reason: "Already scheduled/sent/done" };
  }

  const timestampRaw = candidate.Timestamp;
  if (!timestampRaw) {
    return { eligible: false, reason: "No Timestamp" };
  }

  const formDate = parseTimestamp(timestampRaw);
  if (!formDate || isNaN(formDate.getTime())) {
    return { eligible: false, reason: "Invalid Date" };
  }

  const d1 = new Date(formDate.getFullYear(), formDate.getMonth(), formDate.getDate());
  const d2 = new Date(currentDateUK.getFullYear(), currentDateUK.getMonth(), currentDateUK.getDate());
  const diffDays = Math.floor((d2.getTime() - d1.getTime()) / oneDay);

  if (diffDays < 2) {
    return { eligible: false, reason: `Too soon: ${diffDays} days` };
  }

  return { eligible: true, diffDays };
}

const allItems = $input.all();
